var fs = require('fs');

class Knjiga{
    constructor(id,naziv,autor){
        this.id = id;
        this.naziv = naziv;
        this.autor = autor;
    }
};

class Storage{
    static fileName;
    static SetName(name){
        this.fileName = name;
    }
    static UpdateFile(lista){
        fs.writeFileSync(this.fileName,JSON.stringify(lista));
    }
    static GetKnjige(){
        return JSON.parse(fs.readFileSync(this.fileName));
    }
}

exports.Knjiga = Knjiga;
exports.Storage = Storage;
    